script:
frappe.ui.form.on("Salesorder", {
    refresh:function(frm){
        if(frm.doc.outstanding_amount>0){
            frm.add_custom_button("Make Payment",function(){
                frappe.new_doc('Payment',{
                    sales_order:frm.doc.name,
                    payment_date:frappe.datetime.get_today(),
                    outstanding_amount:frm.doc.outstanding_amount,
                },
                    
                )
            })
        }
     if (!frm.doc.date) {
            frm.set_value("date", frappe.datetime.get_today());
        }

    }
});

 frappe.listview_settings['Salesorder'] = {
    onload(listview) {
        listview.page.add_inner_button("Demo Btn", function() {
            frappe.msgprint("Button in list view!");
        });
         frappe.msgprint("Sales Order onload");
    }
};
 
